mapper.delete:
DELETE
FROM TBL_OneOnOne_BOARD A
WHERE A.OneOnOne_BOARD_NO = #{ no }

mapper.update:
UPDATE
TBL_OneOnOne_BOARD A
SET A.OneOnOne_BOARD_TITLE = #{ title }
, A.OneOnOne_BOARD_BODY = #{ body }
WHERE A.OneOnOne_BOARD_NO = #{ no }

mapper.insert:
INSERT
INTO TBL_ATTACHMENT A
(
  A.ATTACHMENT_NO
, A.REF_BOARD_NO
, A.ORIGINAL_NAME
, A.SAVED_NAME
, A.SAVE_PATH
, A.FILE_TYPE
)
VALUES
(
  null
, #{ refBoardNo }
, #{ originalName }
, #{ savedName }
, #{ savePath }
, #{ fileType }
)
